import React, { useRef, useEffect } from 'react';
import './Experiences.scss';

interface Image {
  src: string;
  alt: string;
  projectName: string;
  companyName: string;
}

interface ExperiencesProps {
  images: Image[];
}

const Experiences: React.FC<ExperiencesProps> = ({ images }) => {
  const textRefs = useRef<(HTMLDivElement | null)[]>([]);

  useEffect(() => {
    let lastScrollTop = 0;

    const handleScroll = () => {
      const fromTop = window.scrollY;
      textRefs.current.forEach((textDiv) => {
        if (textDiv) {
          const speed = 0.3; // Adjust the speed of sliding
          const scrollDirection = fromTop > lastScrollTop ? 1 : -1;
          const transformValue = `translate(-50%, -50%) translateX(${scrollDirection * speed * fromTop}px)`;
          textDiv.style.transform = transformValue;
        }
      });
      lastScrollTop = fromTop <= 0 ? 0 : fromTop; // For Mobile or negative scrolling
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="experiences">
      {images.map((image, index) => (
        <div key={index} className="experience-item">
          <img src={image.src} alt={image.alt} className="experience-image" />
          <div
            className="text-overlay"
            ref={(el) => (textRefs.current[index] = el)}
          >
            <h3 className="project-name">{image.projectName}</h3>
            <p className="company-name">{image.companyName}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Experiences;
