import React, { useRef, useEffect } from 'react';
import './Experiences.scss';

interface Image {
  src: string;
  alt: string;
  projectName: string;
  companyName: string;
}

interface ExperiencesProps {
  images: Image[];
}

const Experiences: React.FC<ExperiencesProps> = ({ images }) => {
  const containerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const handleScroll = () => {
      if (!containerRef.current) return;
      const { top } = containerRef.current.getBoundingClientRect();
      const offset = window.innerHeight / 2;
      const scrollPosition = window.scrollY + offset;
      const position = top - scrollPosition;
      const direction = position > 0 ? 'left' : 'right';
      const factor = Math.abs(position) / window.innerHeight;

      containerRef.current.style.setProperty('--scroll-factor', `${factor}`);
      containerRef.current.style.setProperty('--direction', direction);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="experiences" ref={containerRef}>
      {images.map((image, index) => (
        <div key={index} className="experience-item">
          <img src={image.src} alt={image.alt} className="experience-image" />
          <div className="text-overlay">
            <h3 className="project-name" data-text={image.projectName}>{image.projectName}</h3>
            <p className="company-name">{image.companyName}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Experiences;
