import React, { useRef, useEffect } from 'react';
import './Experiences.scss';

interface Image {
  src: string;
  alt: string;
  projectName: string;
  companyName: string;
}

interface ExperiencesProps {
  images: Image[];
}

const Experiences: React.FC<ExperiencesProps> = ({ images }) => {
  const itemRefs = useRef<HTMLDivElement[]>([]);

  useEffect(() => {
    const handleScroll = () => {
      itemRefs.current.forEach((ref) => {
        if (ref) {
          const rect = ref.getBoundingClientRect();
          const inView = rect.top <= window.innerHeight && rect.bottom >= 0;
          const text = ref.querySelector('.text-overlay');
          if (text) {
            const distanceScrolled = Math.max(0, window.innerHeight - rect.top);
            const percentageInView = Math.min(distanceScrolled / rect.height, 1);
            const maxShift = 30; // maximum shift in pixels
            const shift = maxShift * percentageInView * (rect.top > window.innerHeight ? -1 : 1);
            text.style.transform = `translate(-50%, -50%) translateX(${shift}px)`;
          }
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="experiences">
      {images.map((image, index) => (
        <div key={index} className="experience-item" ref={(el) => (itemRefs.current[index] = el)}>
          <img src={image.src} alt={image.alt} className="experience-image" />
          <div className="text-overlay">
            <h3 className="project-name" data-text={image.projectName}>{image.projectName}</h3>
            <p className="company-name">{image.companyName}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Experiences;
